import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Rooms from './pages/Rooms';
import Booking from './pages/Booking';
import Payments from './pages/Payments';
import Login from './pages/Login';
import AdminLayout from './layouts/AdminLayout';

// Example of a simple authentication check (you can customize it)
const ProtectedRoute = ({ element: Element, ...rest }) => {
  // Replace this with actual logic for checking authentication
  const isAuthenticated = localStorage.getItem('authToken');  // Example using localStorage
  
  // If authenticated, render the requested page; otherwise, redirect to login
  return isAuthenticated ? <Element {...rest} /> : <Navigate to="/login" />;
};

const App = () => (
  <Router>
    <Navbar />
    <Routes>
      {/* Public Routes */}
      <Route path="/" element={<Home />} />
      <Route path="/login" element={<Login />} />

      {/* Admin Routes */}
      <Route path="/admin" element={<AdminLayout />}>
        <Route path="rooms" element={<Rooms />} />
        <Route path="bookings" element={<Booking />} />
        <Route path="payments" element={<Payments />} />
      </Route>

      {/* Protected Route */}
      <Route path="/payments" element={<ProtectedRoute element={Payments} />} />
    </Routes>
  </Router>
);

export default App;
