import React from 'react';

import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useAuth } from './contexts/authContext';
import LoginPage from './pages/LoginPage';  // Your login page component
import DashboardPage from './pages/DashboardPage';  // Example dashboard
import ProtectedRoute from './components/ProtectedRoute'; // Protect routes that need authentication

function App() {
  const { user, loading } = useAuth();

  if (loading) {
    return <div>Loading...</div>;  // Show a loading state until auth is checked
  }

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <DashboardPage />
            </ProtectedRoute>
          }
        />
        {/* Add more protected routes as needed */}
      </Routes>
    </Router>
  );
}

export default App;
