// src/contexts/authContext.js

import React, { createContext, useState, useContext } from 'react';

// Create AuthContext
const AuthContext = createContext();

// Custom hook to use the AuthContext
export const useAuth = () => {
  return useContext(AuthContext);
};

// AuthProvider component to provide authentication state
export const AuthProvider = ({ children }) => {
  const [authState, setAuthState] = useState(null); // null means no user is logged in

  // Function to log in the user
  const login = (user) => {
    setAuthState(user); // Store the logged-in user data
  };

  // Function to log out the user
  const logout = () => {
    setAuthState(null); // Clear the authentication state
  };

  return (
    <AuthContext.Provider value={{ authState, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
